const Todo = require("../models/todo.modles");

async function getAllTodos(req, res, next) {
  let todos;
  try {
    todos = await Todo.getAllTodos();
  } catch (error) {
    return next(error);
  }
  res.json({
    todos: todos,
  });
}

async function addTodo(req, res, next) {
  const todoText = req.body.text;
  //creating the todo
  const todo = new Todo(todoText);
  let insertedId;
  try {
    const result = await todo.save();
    //insertedId field will exist on result object bcz this result object is
    //generated by mongodb and it will hold the id of the todo that was created
    insertedId = result.insertedId;
  } catch (error) {
    return next(error);
  }
  todo.id = insertedId;

  res.json({
    message: "Added todo successfully!",
    createdTodo: {
      text: todo.text,
      id: todo.id,
    },
  });
}

async function udpateTodo(req, res, next) {
  const todoId = req.params.id;
  const newTodoText = req.body.newText;

  const todo = new Todo(newTodoText, todoId);
  try {
    await todo.save();
  } catch (error) {
    return next(error);
  }

  res.json({ message: "Todo updated", udpateTodo: todo });
}


async function deleteTodo(req, res, next) {
  const todoId = req.params.id;

  const todo = new Todo(null,todoId);
  try {
    await todo.delete();
  } catch (error) {
    return next(error);
  }

  res.json({ message: "Todo deleted!"});
}

module.exports = {
  getAllTodos: getAllTodos,
  addTodo: addTodo,
  udpateTodo: udpateTodo,
  deleteTodo: deleteTodo,
};
